import React from "react";
import styles from './style.module.css'
import classNames from "classnames";
import Button from "../button";

export default class UserItem extends React.Component {
  state = {
    active: false,
    value: ''
  }

  handleUserClick = () => {
    this.setState((state) => {
      return {
        active: !state.active
      }
    })
  }

  render() {
    const {userName, img, goToLink, id} = this.props.item

    const userClassNames = classNames(
      styles.user_item,
      {
        [styles.active]: this.state.active
      }
    )
    return (
      <div onClick={this.handleUserClick} className={userClassNames}>
        <img src={img} alt="User Profile"/>
        <h2>
          {userName}
        </h2>
        <Button onClick={this.props.onClickEditButton}>
          {goToLink}
        </Button>
        <button onClick={this.props.onDelete}>on delete</button>
      </div>
    )
  }
}
